import { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [pokemonName, setPokemonName] = useState("")

  useEffect(() => {
    fetch ('https://pokeapi.co/api/v2/pokemon/25/')
    .then((res)=> res.json())
    .then((data)=>{
      console.log(data)
      const {name} = data;
      setPokemonName(name);
    })
    .catch((err)=> console.warn(err))
    return () => {

    }
  }, [])

  return (

    <section>
      <h1>Pokemon:  {pokemonName}</h1>
    </section>
  );
}

export default App;
